import React, { useState } from "react";
import Login from "./Login";
import Signup from "./Signup";

interface AuthContainerProps {
    onAuthenticated: (userData: { email: string; name: string; experienceLevel?: string }) => void;
}

const AuthContainer: React.FC<AuthContainerProps> = ({ onAuthenticated }) => {
    const [currentView, setCurrentView] = useState<"login" | "signup">("login");

    const handleLogin = (userData: { email: string; name: string }) => {
        onAuthenticated(userData);
    };

    const handleSignup = (userData: {
        email: string;
        name: string;
        age: number;
        height: string;
        weight: number;
        experienceLevel: string;
        workoutVolume: string;
        goals: string[];
        equipment: string;
    }) => {
        onAuthenticated({
            email: userData.email,
            name: userData.name,
            experienceLevel: userData.experienceLevel,
        });
    };

    return currentView === "login" ? (
        <Login onLogin={handleLogin} onSwitchToSignup={() => setCurrentView("signup")} />
    ) : (
        <Signup onSignup={handleSignup} onSwitchToLogin={() => setCurrentView("login")} />
    );
};

export default AuthContainer;
