import React from 'react';
import Login from './Login';
import Signup from './Signup';
import { Routes, Route, Navigate, useNavigate } from "react-router-dom";

interface AuthContainerProps {
    onAuthenticated: (userData: { email: string; name: string; experienceLevel?: string }) => void;
}

const AuthContainer: React.FC<AuthContainerProps> = ({ onAuthenticated }) => {
    const navigate = useNavigate();

    const handleLogin = (userData: { email: string; name: string }) => {
        onAuthenticated(userData);
        navigate("/", { replace: true });
    };

    const handleSignup = (userData: any) => {
        onAuthenticated({
            email: userData.email,
            name: userData.name,
            experienceLevel: userData.experienceLevel,
        });
        navigate("/", { replace: true });
    };

    return (
        <Routes>
            <Route path="/" element={<Navigate to="/login" replace />} />

            <Route
                path="/login"
                element={<Login onLogin={handleLogin} onSwitchToSignup={() => navigate("/signup")} />}
            />

            <Route
                path="/signup"
                element={<Signup onSignup={handleSignup} onSwitchToLogin={() => navigate("/login")} />}
            />

            <Route path="*" element={<Navigate to="/login" replace />} />
        </Routes>
    );
};

export default AuthContainer;
